import React,{ Component } from 'react'
import { View, Text, StyleSheet, Image} from 'react-native';
import { Button } from 'react-native-elements';

export default class GerenciarPerfis extends Component {

    render(){
        return(
            <View style={styles.container}>
            <Text style={styles.titulo}>Gerenciar perfis: </Text>
            <View style={{ flexDirection: 'row' }}>
    
    
              <View style={{flexDirection: 'column'}, styles.column}>
                <Image style={styles.imageStyle}
                  source ={require('./assets/dudaGP.png')}>
                </Image>
                
                <View style={{ flexDirection: 'column' }}>
                  <Text style={{ flexDirection: 'row' }}>
                    <Text style={styles.textStyle}>Eduarda</Text>
                  </Text>
                </View>
              </View>
    
              <View style={{ flexDirection: 'column' }, styles.column}>
                <Image style={styles.imageStyle}
                  source ={require('./assets/aliciaGP.png')}>
                </Image>

                <Text style={{ flexDirection: 'row' }}>
                  <Text style={styles.textStyle}>Alícia</Text>
                </Text>
              </View>
              </View>
              <Button style={styles.buttonStyle} title = "Concluído"/>
              <Button style={{},styles.buttonStyle}
        title = "Gerenciar perfis" onPress={()=> 
        {navigation.navigate('GerenciarPerfis')}}/> 
            </View>

        );
    }
}

const styles = StyleSheet.create({
    titulo: {
      fontSize: 70,
      marginBottom: 40,
      marginTop: 1,
      color: 'white',
      textAlign: 'center',
    },
    container: {
      flex: 1,
      backgroundColor: 'black',
      justifyContent: "center",
      alignItems: 'center',
      textAlign: 'center',
    },
  
    textStyle: {
      flex: 1,
      width: "50%",
      color: 'gray',
      fontSize: 25,
      textAlign: "center",
      justifyContent: "center",
    },
    imageStyle: {
      marginBottom: 10,
      alignItems: "center",
      width: 200,
      height: 200,
      resizeMode: "center",
      rowStyle: 'center',
   
      borderRadius: 5,
      marginLeft: 5,
    },
    column: {
      marginLeft: 50,
    },
    buttonStyle: {
      backgroundColor: 'red', 
      borderWidth: 0.5, 
      borderColor: 'grey', 
      height: 50,
      width: 220,
      margin: 50, 
      textAlign: 'center',
    },
  });
